svg.script:
// 

borderPath = templateHooks.borderPath = function( width, height, offset, up ){
	var path = '';

	if( width > 0 && height > 0 ){

		var finalWidth = width - 2 * offset;
		var finalHeight = height - offset;

		if( up ){

			path =	"M " + offset + " " + ( finalHeight + offset ) + " " +
					"l " + 0 + " " + ( -finalHeight ) + " " +
					"l " + finalWidth + " " + 0 + " " +
					"l " + 0 + " " + finalHeight;
		} else {
			path =	"M " + offset + " " + ( height + 2 ) + " " +
					"l " + 0 + " " + finalHeight + " " +
					"l " + finalWidth + " " + 0 + " " +
					"l " + 0 + " " + (-finalHeight);
		}
	}

	return path;
};

shapeWidth	= handle_width - 1;
shapeHeight	= (handle_height - 1) / 2 - 1;

//
